        let recipe = new Recipe(json.name, json.author, json.guestsNumber, json.recipeCategory.id);
        recipe.id = json.id;
        recipe.recipeCategoryName = json.recipeCategory.name;
        return recipe;
    }
    selectRecipe(selectedRecipe) {
        this.currentRecipe = selectedRecipe;
    }
    async getOneRecipe() {
        let id = this.currentRecipe.id;
        let recipe = await this.httpService.get(`http://localhost:3000/recipe/${id}`).pipe(map(json => this.jsonToRecipe(json)));
        return recipe;
    }
    getAllRecipes() {
        return this.httpService.get("http://localhost:3000/recipe").pipe(map(data => data.map(json => this.jsonToRecipe(json))));
    }
    createRecipe(recipe) {
        return this.httpService.post("http://localhost:3000/recipe", recipe);
    }
    deleteRecipe(id) {
        return this.httpService.delete(`http://localhost:3000/recipe/${id}`);
    }
}
